import "./App.css";
import { Component } from "react";

class App extends Component {
  constructor() {
    super();

    this.state = {
      monster1: {
        name: "Linda",
      },
      monster2: {
        name: "Frank"
      },
      monster3: {
        name: "Jacky"
      }
    };
  }
  render() {
    return (
      <div className="App">
        <header className="App-header">
          <p>
            hi {this.state.name.firstname} {this.state.name.lastname}, I work at{" "}
            {this.state.company}
          </p>
          <button
            onClick={() => {
              this.setState((state,props)=>{
                return {
                   name: {firstname:"David", lastname:"David"}
                }
              },
               ()=>{
                 console.log(this.state)
               });
            }}
          >
            Change Name
          </button>
        </header>
      </div>
    );
  }
}

export default App;
